from docx.shared import Pt, Cm, RGBColor
# Embedded Word template (FORMAT BETUL COLLEAGUE !.docx) as base64.
# This avoids requiring a separate template file in the repository.
_TEMPLATE_B64 = """

""".strip()

# --------------------------------------------------------------------
# Utilities
# --------------------------------------------------------------------

from docx.enum.text import WD_ALIGN_PARAGRAPH
from docx.oxml import OxmlElement

def _delete_paragraph(paragraph):
    p = paragraph._element
    p.getparent().remove(p)

def _remove_leading_empty_paragraphs(doc: Document, max_remove: int = 5):
    removed = 0
    while removed < max_remove and doc.paragraphs and not doc.paragraphs[0].text.strip():
        _delete_paragraph(doc.paragraphs[0])
        removed += 1

def _set_para_compact(p):
    p.paragraph_format.space_before = Pt(0)
    p.paragraph_format.space_after = Pt(0)
    p.paragraph_format.line_spacing = 1

def _ensure_rujukan_in_header(doc: Document, rujukan_kami: str, tarikh_str: str):
    for section in doc.sections:
        section.different_first_page_header_footer = False
        hdr = section.header

        updated_any = False
        for p in hdr.paragraphs:
            t = (p.text or "").strip()
            if t.startswith("Rujukan Tuan"):
                _set_paragraph_text(p, "Rujukan Tuan :", color_rgb=(0,0,0))
                p.alignment = WD_ALIGN_PARAGRAPH.RIGHT
                _set_para_compact(p)
                updated_any = True
            elif t.startswith("Rujukan Kami"):
                _set_paragraph_text(p, f"Rujukan Kami : {rujukan_kami}", color_rgb=(0,0,0))
                p.alignment = WD_ALIGN_PARAGRAPH.RIGHT
                _set_para_compact(p)
                updated_any = True
            elif t.startswith("Tarikh"):
                _set_paragraph_text(p, f"Tarikh : {tarikh_str}", color_rgb=(0,0,0))
                p.alignment = WD_ALIGN_PARAGRAPH.RIGHT
                _set_para_compact(p)
                updated_any = True

        if not updated_any:
            p1 = hdr.add_paragraph("Rujukan Tuan :")
            p2 = hdr.add_paragraph(f"Rujukan Kami : {rujukan_kami}")
            p3 = hdr.add_paragraph(f"Tarikh : {tarikh_str}")
            for p in (p1, p2, p3):
                p.alignment = WD_ALIGN_PARAGRAPH.RIGHT
                _set_para_compact(p)
                for r in p.runs:
                    _set_run_font(r, name="Arial", size_pt=11, bold=False, color_rgb=(0,0,0))

def _fix_keputusan_table(doc: Document):
    target = None
    for tbl in doc.tables:
        all_text = "\n".join(
            "\n".join(p.text for p in cell.paragraphs)
            for row in tbl.rows for cell in row.cells
        ).upper()
        if "LULUS" in all_text and "TOLAK" in all_text and "TANGGUH" in all_text:
            target = tbl
            break
    if target is None:
        return

    target.autofit = False

    cb_w = Cm(0.8)
    label_w = Cm(7.2)

    for row in target.rows:
        for cell in row.cells:
            cell.vertical_alignment = WD_ALIGN_VERTICAL.CENTER
            for p in cell.paragraphs:
                _set_para_compact(p)

    for r in range(len(target.rows)):
        target.cell(r, 0).width = cb_w
        target.cell(r, 1).width = label_w
        target.cell(r, 2).width = cb_w
        target.cell(r, 3).width = label_w

    for r in range(len(target.rows)):
        for c in (1,3):
            for p in target.cell(r, c).paragraphs:
                for run in p.runs:
                    _set_run_font(run, name="Arial", size_pt=11, bold=None, color_rgb=(0,0,0))



_BULAN_MAP = {
    "JANUARI": 1,
    "FEBRUARI": 2,
    "MAC": 3,
    "APRIL": 4,
    "MEI": 5,
    "JUN": 6,
    "JULAI": 7,
    "OGOS": 8,
    "SEPTEMBER": 9,
    "OKTOBER": 10,
    "NOVEMBER": 11,
    "DISEMBER": 12,
}

_BULAN_TITLE = {
    1:"Januari", 2:"Februari", 3:"Mac", 4:"April", 5:"Mei", 6:"Jun",
    7:"Julai", 8:"Ogos", 9:"September", 10:"Oktober", 11:"November", 12:"Disember"
}

_HARI_MAP = {
    0:"Isnin",
    1:"Selasa",
    2:"Rabu",
    3:"Khamis",
    4:"Jumaat",
    5:"Sabtu",
    6:"Ahad",
}

_FIELD_PREFIXES = [
    "PEMOHON",
    "PERUNDING",
    "LOKASI",
    "KOORDINAT",
    "NO. RUJUKAN OSC",
    "NO. RUJUKAN",
    "PELAN SUSUNATUR",
    "NO FAIL",
    "NO. FAIL",
]

def _clean(s: str) -> str:
    s = s.replace("\u00a0", " ")
    s = re.sub(r"\s+", " ", s).strip()
    return s

def _to_date_string(dt: datetime.date) -> str:
    return f"{dt.day} {_BULAN_TITLE[dt.month]} {dt.year}"

def _safe_filename(s: str, max_len: int = 80) -> str:
    # keep letters, numbers, spaces, underscore
    s = unicodedata.normalize("NFKD", s)
    s = re.sub(r"[\s]+", "_", s.strip())
    s = re.sub(r"[^A-Za-z0-9_]+", "", s)
    s = s.strip("_")
    if not s:
        s = "pemohon"
    return s[:max_len]

def _load_template() -> Document:
    data = base64.b64decode(_TEMPLATE_B64)
    return Document(io.BytesIO(data))

def _set_run_font(run, name="Arial", size_pt=11, bold=None, color_rgb=(0,0,0)):
    run.font.name = name
    run._element.rPr.rFonts.set(qn("w:eastAsia"), name)
    run.font.size = Pt(size_pt)
    # Force black (avoid theme/grey rendering)
    if color_rgb is not None:
        run.font.color.rgb = RGBColor(*color_rgb)
    if bold is not None:
        run.bold = bold

# qn import (only when needed)
from docx.oxml.ns import qn

def _set_paragraph_text(paragraph, text: str, name="Arial", size_pt=11, bold=False, color_rgb=(0,0,0)):
    # Clear existing runs but keep paragraph formatting (indent/alignment/spacing).
    for r in list(paragraph.runs):
        r.text = ""
    paragraph.text = ""
    run = paragraph.add_run(text)
    _set_run_font(run, name=name, size_pt=size_pt, bold=bold, color_rgb=color_rgb)
    return paragraph
def _set_cell_text(cell, text: str, name="Arial", size_pt=11, bold=False, preserve_linebreaks=True, color_rgb=(0,0,0), align=None):
    # Clear existing paragraphs
    for p in cell.paragraphs:
        for r in list(p.runs):
            r.text = ""
        p.text = ""
    # Use first paragraph
    p = cell.paragraphs[0]
    if align is not None:
        p.alignment = align
    if preserve_linebreaks and "\n" in text:
        run = p.add_run("")
        _set_run_font(run, name=name, size_pt=size_pt, bold=bold, color_rgb=color_rgb)
        parts = text.split("\n")
        for idx, part in enumerate(parts):
            if idx > 0:
                run.add_break()  # line break within same paragraph
            run.add_text(part)
    else:
        run = p.add_run(text)
        _set_run_font(run, name=name, size_pt=size_pt, bold=bold, color_rgb=color_rgb)

def _parse_meeting_info(paras: list[str]) -> tuple[str|None, datetime.date|None]:
    full = "\n".join(paras)

    m_bil = re.search(r"\bBIL\.?\s*[:.-]?\s*(\d{1,2}/\d{4})", full, flags=re.IGNORECASE)
    bil = m_bil.group(1) if m_bil else None

    # Find first date in Malay uppercase form (e.g., 12 JANUARI 2026)
    m_date = re.search(r"(\d{1,2})\s+([A-Z]{3,})\s+(\d{4})", full)
    if not m_date:
        return bil, None

    day = int(m_date.group(1))
    mon_name = m_date.group(2).upper()
    year = int(m_date.group(3))
    if mon_name not in _BULAN_MAP:
        return bil, None

    try:
        dt = datetime.date(year, _BULAN_MAP[mon_name], day)
    except Exception:
        dt = None
    return bil, dt

def _split_blocks(paras: list[str]) -> list[tuple[str, list[str]]]:
    blocks = []
    i = 0
    while i < len(paras):
        line = paras[i]
        if line.upper().startswith("KERTAS MESYUARAT BIL."):
            kertas = re.sub(r"^KERTAS MESYUARAT BIL\.?\s*", "", line, flags=re.IGNORECASE).strip()
            j = i + 1
            block_lines = []
            while j < len(paras) and not paras[j].upper().startswith("KERTAS MESYUARAT BIL."):
                block_lines.append(paras[j])
                j += 1
            blocks.append((kertas, block_lines))
            i = j
        else:
            i += 1
    return blocks

def _extract_value(line: str) -> str:
    # supports "Pemohon : X" or "Pemohon	:	X"
    parts = re.split(r"\s*:\s*", line, maxsplit=1)
    if len(parts) == 2:
        return parts[1].strip()
    # fallback: remove label
    return re.sub(r"^[A-Za-z .()/-]+\s+", "", line).strip()

def _strip_parentheses(s: str) -> str:
    # remove any "(...)" and trim
    s = re.sub(r"\s*\([^)]*\)", "", s).strip()
    return s

def _parse_case(kertas: str, block_lines: list[str]) -> dict|None:
    kertas_clean = _clean(kertas)

    if "OSC/PKM/" in kertas_clean:
        jenis = "Kebenaran Merancang"
        jenis_code = "PKM"
    elif "OSC/BGN/" in kertas_clean:
        jenis = "Bangunan"
        jenis_code = "BGN"
    else:
        return None

    # Description/Nama Permohonan = all lines from start until first field-prefix line (Pemohon/Perunding/etc.)
    desc_lines = []
    first_field_idx = None
    for idx, raw in enumerate(block_lines):
        l = raw.strip()
        if not l:
            continue
        if l.upper().startswith(tuple(_FIELD_PREFIXES)):
            first_field_idx = idx
            break
        desc_lines.append(raw.rstrip())

    nama_permohonan = "\n".join([dl.strip() for dl in desc_lines if dl.strip()]).strip()

    pemohon = ""
    perunding = ""
    no_ruj_osc = ""

    for raw in block_lines:
        l = raw.strip()
        if not l:
            continue
        u = l.upper()
        if u.startswith("PEMOHON"):
            pemohon = _strip_parentheses(_extract_value(l))
        elif u.startswith("PERUNDING"):
            perunding_raw = _strip_parentheses(_extract_value(l))
            # Usually we want firm only (remove person name like "Ar. ...")
            perunding = re.split(r"\s+Ar\.", perunding_raw, maxsplit=1)[0].strip() or perunding_raw
        elif u.startswith("NO. RUJUKAN OSC"):
            no_ruj_osc = _extract_value(l).strip()

    # If empty perunding, fallback to pemohon? (should not happen)
    return {
        "kertas": kertas_clean,
        "jenis_code": jenis_code,
        "jenis_permohonan": jenis,
        "pemohon": pemohon,
        "perunding": perunding,
        "nama_permohonan": nama_permohonan,
        "id_permohonan": no_ruj_osc,
    }

def parse_agenda(doc: Document) -> dict:
    paras = [p.text for p in doc.paragraphs if p.text and p.text.strip()]
    paras = [p.replace("\u00a0", " ").rstrip() for p in paras]

    meeting_bil, meeting_date = _parse_meeting_info([_clean(p) for p in paras])

    blocks = _split_blocks(paras)
    cases = []
    for kertas, lines in blocks:
        c = _parse_case(kertas, lines)
        if c:
            cases.append(c)

    # split PKM and BGN order as in agenda: keep original order, but we can also interleave if needed.
    return {
        "meeting_bil": meeting_bil,
        "meeting_date": meeting_date,
        "cases": cases,
    }

def _fill_doc(case: dict, meeting_bil: str, meeting_date: datetime.date, case_no: int) -> Document:
    doc = _load_template()

    # Remove top empty paragraph that creates an extra blank gap after header
    _remove_leading_empty_paragraphs(doc)

    # Meeting date strings
    tarikh_str = _to_date_string(meeting_date)
    hari_str = _HARI_MAP[meeting_date.weekday()]

    # Agenda number/year for Rujukan Kami
    year = str(tarikh.year) if hasattr(tarikh, "year") else str(tarikh)[-4:]
    # Nombor dalam kurungan kedua ikut turutan keseluruhan dokumen (1..38)
    seq_no = str(case_no)
    rujukan_kami = f"({case_no})MBSP/15/1551/({seq_no}){year}"
    tarikh_text = tarikh.strftime("%d %B %Y") if hasattr(tarikh, "strftime") else str(tarikh)

    # Update existing body placeholders (if any) to black, no extra spacing
    for p in doc.paragraphs:
        t = (p.text or "").strip()
        if t.startswith("Rujukan Tuan"):
            _set_paragraph_text(p, "Rujukan Tuan :", color_rgb=(0,0,0))
            p.paragraph_format.space_before = Pt(0)
            p.paragraph_format.space_after = Pt(0)
        elif t.startswith("Rujukan Kami"):
            _set_paragraph_text(p, f"Rujukan Kami : {rujukan_kami}", color_rgb=(0,0,0))
            p.paragraph_format.space_before = Pt(0)
            p.paragraph_format.space_after = Pt(0)
        elif t.startswith("Tarikh"):
            _set_paragraph_text(p, f"Tarikh : {tarikh_text}", color_rgb=(0,0,0))
            p.paragraph_format.space_before = Pt(0)
            p.paragraph_format.space_after = Pt(0)

    # Ensure header repeats on page 2+ and remove (blank) any body copies to avoid extra gap
    _ensure_rujukan_in_header(doc, rujukan_kami, tarikh_text)

    return doc


def build_zip(agenda_doc: Document) -> tuple[bytes, dict]:
    parsed = parse_agenda(agenda_doc)
    meeting_bil = parsed["meeting_bil"]
    meeting_date = parsed["meeting_date"]
    cases = parsed["cases"]

    if not meeting_bil or not meeting_date:
        raise ValueError("Tak jumpa BIL. (cth: 01/2026) atau tarikh mesyuarat (cth: 12 JANUARI 2026) dalam agenda.")

    # Generate docs
    buf = io.BytesIO()
    with zipfile.ZipFile(buf, "w", compression=zipfile.ZIP_DEFLATED) as z:
        for idx, case in enumerate(cases, start=1):
            doc = _fill_doc(case, meeting_bil, meeting_date, idx)
            pemohon_short = _safe_filename(case.get("pemohon","pemohon"))
            # kertas like OSC/PKM/01/2026 => OSC_PKM_01_2026
            k = case.get("kertas","").replace("/", "_")
            filename = f"{idx:02d}_{k}_{pemohon_short}.docx"
            fbuf = io.BytesIO()
            doc.save(fbuf)
            z.writestr(filename, fbuf.getvalue())

    meta = {
        "meeting_bil": meeting_bil,
        "meeting_date": _to_date_string(meeting_date),
        "count_total": len(cases),
        "count_pkm": sum(1 for c in cases if c.get("jenis_code")=="PKM"),
        "count_bgn": sum(1 for c in cases if c.get("jenis_code")=="BGN"),
    }
    return buf.getvalue(), meta

